import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import './Calendar.css';

const Calendar = () => {
  const [events, setEvents] = useState([
    {
      id: '1',
      title: 'Review Client Intake Forms',
      date: '2024-01-15',
      backgroundColor: '#e74c3c',
      borderColor: '#e74c3c',
      textColor: '#fff',
      extendedProps: {
        taskType: 'overdue',
        priority: 'high'
      }
    },
    {
      id: '2',
      title: 'Complete Risk Assessment',
      date: '2024-01-20',
      backgroundColor: '#f1c40f',
      borderColor: '#f1c40f',
      textColor: '#fff',
      extendedProps: {
        taskType: 'pending',
        priority: 'medium'
      }
    },
    {
      id: '3',
      title: 'Update Care Plan',
      date: '2024-01-25',
      backgroundColor: '#3498db',
      borderColor: '#3498db',
      textColor: '#fff',
      extendedProps: {
        taskType: 'in-progress',
        priority: 'low'
      }
    }
  ]);

  const handleDateClick = (arg) => {
    console.log('Date clicked:', arg.dateStr);
    // You can add logic here to create new tasks on date click
  };

  const handleEventClick = (arg) => {
    console.log('Event clicked:', arg.event.title);
    // You can add logic here to edit/view task details
  };

  const handleEventDrop = (arg) => {
    console.log('Event dropped:', arg.event.title, 'to', arg.event.startStr);
    // You can add logic here to update task due dates
  };

  return (
    <div className="calendar-container">
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        headerToolbar={{
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth,timeGridWeek,timeGridDay'
        }}
        initialView="dayGridMonth"
        editable={true}
        selectable={true}
        selectMirror={true}
        dayMaxEvents={true}
        weekends={true}
        events={events}
        dateClick={handleDateClick}
        eventClick={handleEventClick}
        eventDrop={handleEventDrop}
        height="auto"
        eventDisplay="block"
        eventTimeFormat={{
          hour: '2-digit',
          minute: '2-digit',
          meridiem: 'short'
        }}
      />
    </div>
  );
};

export default Calendar;
