import React, { useState } from 'react';
import './Home.css';

const Home = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({
    username: '',
    password: ''
  });

  const handleLogin = (e) => {
    e.preventDefault();
    // Here you would typically make an API call to authenticate
    if (loginForm.username && loginForm.password) {
      setIsLoggedIn(true);
      setLoginForm({ username: '', password: '' });
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const handleInputChange = (e) => {
    setLoginForm({
      ...loginForm,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="home">
      <h1>Welcome to Social Care 365</h1>
      <p>Your All in One Social Care management application.</p>

      {!isLoggedIn ? (
        <div className="login-section">
          <h2>Login</h2>
          <form onSubmit={handleLogin} className="login-form">
            <div className="form-group">
              <label htmlFor="username">Username:</label>
              <input
                type="text"
                id="username"
                name="username"
                value={loginForm.username}
                onChange={handleInputChange}
                required
                className="form-input"
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="password"
                name="password"
                value={loginForm.password}
                onChange={handleInputChange}
                required
                className="form-input"
              />
            </div>
            <button type="submit" className="login-btn">
              Login
            </button>
          </form>
        </div>
      ) : (
        <div className="dashboard">
          <div className="dashboard-header">
            <h2>Dashboard</h2>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </div>

          <div className="features">
            <h3>Key Features</h3>
            <div className="features-grid">
              <div className="feature-card">
                <h4>Task Management</h4>
                <p>Organize and track all your care tasks efficiently</p>
              </div>
              <div className="feature-card">
                <h4>Client Records</h4>
                <p>Maintain comprehensive client information and care plans</p>
              </div>
              <div className="feature-card">
                <h4>Staff Scheduling</h4>
                <p>Manage staff rosters and assignments seamlessly</p>
              </div>
              <div className="feature-card">
                <h4>Reporting</h4>
                <p>Generate detailed reports and analytics</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
