import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Logo from '../components/Logo';
import './Tasks.css';

const Tasks = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();
  const [showUploadMenu, setShowUploadMenu] = useState(false);
  const [showProfileMenu, setShowProfileMenu] = useState(false);

  // Sample data for the Kanban board
  const [kanbanData, setKanbanData] = useState({
    Backlog: [
      { id: 1, title: 'Review client intake forms' },
      { id: 2, title: 'Prepare care plan draft' },
      { id: 3, title: 'Schedule initial assessment' }
    ],
    'In Progress': [
      { id: 4, title: 'Home visit - Mrs. Johnson' },
      { id: 5, title: 'Update care records' }
    ],
    Blocked: [
      { id: 6, title: 'Awaiting family consent' },
      { id: 7, title: 'Medical records pending' }
    ],
    Done: [
      { id: 8, title: 'Initial assessment completed' },
      { id: 9, title: 'Care plan approved' }
    ]
  });

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  const handleUploadAction = (action) => {
    setShowUploadMenu(false);
    if (action === 'create-task') {
      console.log('Create new task clicked');
      // Add your create task logic here
    } else if (action === 'upload-form') {
      console.log('Upload meeting form clicked');
      // Add your upload form logic here
    }
  };

  const handleProfileAction = (action) => {
    setShowProfileMenu(false);
    if (action === 'settings') {
      console.log('Settings clicked');
      // Add your settings logic here
    } else if (action === 'logout') {
      handleLogout();
    }
  };

  const handleCreateTask = (column = null) => {
    console.log(`Create task${column ? ` in ${column}` : ''} clicked`);
    // Add your create task logic here
    // If column is provided, you can pre-select the column for the new task
  };

  return (
    <div className="tasks-page">
      <div className="tasks-header">
        <div className="header-left">
          <div className="tasks-logo">
            <Logo size="small" />
          </div>
          <nav className="header-nav">
            <button className="nav-btn">Cases</button>
            <button className="nav-btn active">Tasks</button>
          </nav>
        </div>

        <div className="header-right">
          {/* Upload Floating Action Button */}
          <div className="fab-container">
            <button 
              className="fab fab-upload"
              onMouseEnter={() => setShowUploadMenu(true)}
              onMouseLeave={() => setShowUploadMenu(false)}
            >
              <span className="fab-icon">📤</span>
              <span className="fab-label">Upload Form</span>
            </button>

            {showUploadMenu && (
              <div className="fab-menu">
                <button 
                  className="fab-menu-item"
                  onClick={() => handleUploadAction('create-task')}
                >
                  <span className="menu-icon">➕</span>
                  Create New Task
                </button>
                <button 
                  className="fab-menu-item"
                  onClick={() => handleUploadAction('upload-form')}
                >
                  <span className="menu-icon">📋</span>
                  Upload Meeting Form
                </button>
              </div>
            )}
          </div>

          {/* Profile Floating Action Button */}
          <div className="fab-container">
            <button 
              className="fab fab-profile"
              onMouseEnter={() => setShowProfileMenu(true)}
              onMouseLeave={() => setShowProfileMenu(false)}
            >
              <span className="fab-icon">👤</span>
              <span className="fab-label">Profile</span>
            </button>

            {showProfileMenu && (
              <div className="fab-menu">
                <button 
                  className="fab-menu-item"
                  onClick={() => handleProfileAction('settings')}
                >
                  <span className="menu-icon">⚙️</span>
                  Settings
                </button>
                <button 
                  className="fab-menu-item"
                  onClick={() => handleProfileAction('logout')}
                >
                  <span className="menu-icon">🚪</span>
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Tasks Content */}
      <div className="tasks-content">
        <div className="tasks-header-section">
          <div className="tasks-title-section">
            <h1>Tasks Management</h1>
            <p>Manage and track all your care tasks efficiently</p>
          </div>
          <button 
            className="create-task-btn"
            onClick={() => handleCreateTask()}
          >
            <span className="btn-icon">➕</span>
            Create Task
          </button>
        </div>

        {/* Kanban Board */}
        <div className="kanban-section">
          <div className="kanban-board">
            {Object.keys(kanbanData).map((column) => (
              <div className="kanban-column" key={column}>
                <div className="kanban-column-header">
                  <h3 className="kanban-column-title">{column}</h3>
                  <span className="kanban-column-count">{kanbanData[column].length}</span>
                </div>
                <div className="kanban-tasks">
                  {kanbanData[column].map((task) => (
                    <div className="kanban-task" key={task.id}>
                      <div className="task-title">{task.title}</div>
                    </div>
                  ))}
                </div>
                <button 
                  className="column-create-task-btn"
                  onClick={() => handleCreateTask(column)}
                >
                  <span className="btn-icon">➕</span>
                  Add Task
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Tasks;
